import React from 'react';
import './App.css';
import { TaskType, Todolist } from './components/Todolist/Todolist/Todolist';
import { v1 } from 'uuid';
import AddNewTask from './components/AddNewTask/AddNewTask';
import { AppBar, Button, Container, Grid, IconButton, Paper, Toolbar, Typography } from '@mui/material';
import { MenuBook } from '@mui/icons-material';
import { addTodolistAC, changeNewFilterAC, changeTodolistTitleAC, removeTodolistAC} from './state/todolists-reducer';
import {changeCheckedTaskAC, removeTaskAC} from './state/tasks-reducer';
import { useDispatch } from 'react-redux';
import { AppRootState } from './state/store';
import { useSelector } from 'react-redux';
export type FilterValuesType ="all" | "completed" | "active"
export type TodolistType = {
  id:string
  title:string
  filter:FilterValuesType
}
export type TaskStateType = {
  [key: string]: Array<TaskType>
}
function AppWithReducers() {
  const dispatch = useDispatch()
  const todolists = useSelector<AppRootState, Array<TodolistType>>(state=>state.todolists)
  const tasks = useSelector<AppRootState, TaskStateType>(state=>state.tasks)


  const removeTask = (id:string,todolistId:string)=>{
    dispatch(removeTaskAC(todolistId,id))
  }
  const changeTodolistTitle = (todolistId:string, newValue:string)=>{
    dispatch(changeTodolistTitleAC(todolistId,newValue))
  }
  const changeFilter = (filter:FilterValuesType, todolistId:string)=>{
    dispatch(changeNewFilterAC(todolistId,filter))
  }
  const addTodolist = (title:string)=>{
    dispatch(addTodolistAC(title))
  }
  const removeTodolist = (id:string)=>{
    dispatch(removeTodolistAC(id))
  }
  return (
    <div className="App">
      <AppBar position='static'>
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu">
            <MenuBook/>
          </IconButton>
          <Typography variant='h6'>
            News
          </Typography>
          <Button color='inherit'>login</Button>
        </Toolbar>
      </AppBar>
      <Container fixed >
        <div className='add-new-todolists'>
          <Grid container>
            <AddNewTask addItem={addTodolist}/>
          </Grid>
        </div>
        <Grid container spacing={5}>
        {todolists.map((tl)=>{
          let tasksForToDoList = tasks[tl.id]
          if(tl.filter==="completed"){
            tasksForToDoList=tasks[tl.id].filter((t:TaskType)=>{return t.isDone===true})
          }

          if(tl.filter==="active"){
            tasksForToDoList=tasks[tl.id].filter((t:TaskType)=>{return t.isDone===false})
          }
        return (
          <Grid item>
            <Paper>
              <Todolist 
              key={tl.id}
              title={tl.title} 
              removeTask={removeTask}
              changeFilter={changeFilter}
              filter={tl.filter}
              id={tl.id}
              removeTodolist={removeTodolist}
              changeTodolistTitle={changeTodolistTitle}/>
            </Paper>
          </Grid>
          )
      })}
      </Grid>
      </Container>
    </div>
  );
}
export default AppWithReducers;
