import React from "react";
import './EditableSpan.css'

type EditableSpanPropsType = {
    title:string
    isDone:boolean
    editMode: boolean
}

const EditableSpan = ({title, isDone,editMode}:EditableSpanPropsType) =>{
    return (
        <>
            <span 
            className={isDone ? 'is-done' : ''}>
                {title}
            </span>
        </>
    )
}

export default EditableSpan
