import React, { useState } from 'react';
import './App.css';
import { TaskType, Todolist } from './components/Todolist/Todolist/Todolist';
import { v1 } from 'uuid';

export type FilterValuesType ="all" | "completed" | "active"

function App() {
  let task = [
    {id:v1(), title:'CSS', isDone:true},
    {id:v1(), title:'JS', isDone:true},
    {id:v1(), title:'React', isDone:false},
    {id:v1(), title:'Redux', isDone:false}
  ]

  let [tasks,setTasks]=useState< Array <TaskType> >(task)
  let [filter,setFilter] = useState< FilterValuesType >("all")
  let [error,setError]=useState(false)

  const addNewTask= (value:string) =>{
    if(value.trim().length!==0){
      let newTasks = [...tasks]
      newTasks.unshift({id:v1(), title: value.trim(), isDone:false})
      setTasks(newTasks)
      setError(false)
    }else{
      setError(true)
    }
  }

  const changeChecked= (id:string) =>{
    let task = tasks.find((el) => el.id===id)
    if (task)  task.isDone=!task.isDone
    let newTasks=[...tasks]
    setTasks(newTasks)
  }

  let tasksForToDoList = tasks
  if(filter==="completed"){
    tasksForToDoList=tasks.filter((t:TaskType)=>{return t.isDone===true})
  }

  if(filter==="active"){
    tasksForToDoList=tasks.filter((t:TaskType)=>{return t.isDone===false})
  }

  let removeTask = (id:string)=>{
    setTasks(tasks=tasks.filter((t:TaskType) => {return (t.id!==id)}))
  }

  let changeFilter = (filter:FilterValuesType)=>{
    setFilter(filter)
  }
  return (
    <div className="App">
      <Todolist title={'What to learn'} 
        tasks={tasksForToDoList} 
        removeTask={removeTask}
        changeFilter={changeFilter}
        addNewTask={addNewTask}
        changeChecked={changeChecked}
        error={error}
        setError={setError}
        filter={filter}/>
    </div>
  );
}


export default App;
