import React, { ChangeEvent, useState } from "react";
import './EditableSpan.css'

type EditableSpanPropsType = {
    title:string
    isDone:boolean
    todolistId:string
    taskId:string
    changeTaskTitle:(taskId:string,todolistId:string, newValue:string)=>void
}

const EditableSpan = ({title, isDone, todolistId, taskId, changeTaskTitle}:EditableSpanPropsType) =>{
    const [editMode, setEditMode] = useState(true)
    let [newValue, setnewValue] = useState('')
    const activateEditMode = ()  => {
        setEditMode(false)
        setnewValue(title)
    }
    const activateViewMode = ()  => {
        setEditMode(true)
        changeTaskTitle(taskId,todolistId,newValue)
    }

    const changeValueHandler = (e:ChangeEvent<HTMLInputElement>) => setnewValue(e.target.value)


    return (
        <>
            {
                editMode ? 
                <span 
                onDoubleClick={activateEditMode}
                className={isDone ? 'is-done' : ''}>
                    {title}
                </span>  
                : <input value={newValue} 
                onChange={changeValueHandler}
                onBlur={activateViewMode}// событие onBlur возникает при потере элемента из фокуса
                autoFocus/> // ставит автоматичиски фокус на input
            }
        </>
    )
}

export default EditableSpan
