import React, { useState } from 'react';
import './App.css';
import { TaskType, Todolist } from './components/Todolist/Todolist/Todolist';

export type FilterValuesType ="all"|"completed"|"active"

function App() {
  let [tasks,setTasks]=useState([
    {id:1, title:'CSS', isDone:true},
    {id:2, title:'JS', isDone:true},
    {id:3, title:'React', isDone:false},
    {id:4, title:'Redux', isDone:false}
  ])

  let [filter,setFilter] = useState("all")

  let tasksForToDoList = tasks

  if(filter==="completed"){
    tasksForToDoList=tasks.filter((t:TaskType)=>{return t.isDone===true})
  }

  if(filter==="active"){
    tasksForToDoList=tasks.filter((t:TaskType)=>{return t.isDone===false})
  }

  let removeTask = (id:number)=>{
    setTasks(tasks=tasks.filter((t:TaskType) => {return (t.id!==id)}))
  }

  let removeAllTask = ()=>{
    setFilter('all')
  }
  let removeCompletedTask = ()=>{
    setFilter('completed')
  }
  let removeActiveTask = ()=>{
    setFilter('active')
  }
  return (
    <div className="App">
      <Todolist title={'What to learn'} 
        tasks={tasksForToDoList} 
        removeTask={removeTask}
        removeAllTask={removeAllTask}
        removeCompletedTask={removeCompletedTask}
        removeActiveTask={removeActiveTask}/>
    </div>
  );
}


export default App;
