import React, { KeyboardEvent,ChangeEvent, useState } from 'react';
import { FilterValuesType } from "../../../App"
import './Todolist.css'
export type TaskType = {
    id:string
    title:string
    isDone:boolean
}
type PropsType = {
    title:string
    tasks:Array<TaskType>
    removeTask: (id:string)=> void
    changeFilter: (filter:FilterValuesType)=> void
    addNewTask:(value:string)=>void
    changeChecked:(id:string)=>void
    error:boolean
    setError:(boolean:boolean)=>void
}

export const Todolist = ({title,tasks,removeTask,changeFilter,addNewTask,changeChecked,error,setError}:PropsType)=>{
    const [newTask,setNewTask] = useState("")
    const [allBtn, setAllBtn]=useState(true)
    const [activeBtn, setActiveBtn]=useState(false)
    const [completedBtn, setCompletedBtn]=useState(false)
    const CreateNewTask = (value:string)=>{
        setNewTask(value)
        setError(false)
    }
    const onNewTitleChangeHandler = (e:ChangeEvent<HTMLInputElement>)=>{
        CreateNewTask(e.target.value)
    }
    const onEnterKeyPressHandler = (e:KeyboardEvent<HTMLInputElement>)=>{
                    if (e.charCode ===13){
                        addNewTask(newTask)
                        setNewTask('')
                    }
                }
    const addNewTaskHandler  = ()=>{
        addNewTask(newTask)
        setNewTask('')
    }
    const changeFilterAllHandler = () => {
        changeFilter('all')
        setAllBtn(true)
        setActiveBtn(false)
        setCompletedBtn(false)
    }
    const changeFilterActiveHandler = () => {
        changeFilter('active')
        setAllBtn(false)
        setActiveBtn(true)
        setCompletedBtn(false)
    }

    const changeFilterCompletedHandler = () => {
        changeFilter('completed')
        setAllBtn(false)
        setActiveBtn(false)
        setCompletedBtn(true)
    }

    return (
        <div>
            <h3>{title}</h3>
            <div>
                <input 
                onChange={onNewTitleChangeHandler} 
                onKeyPress={onEnterKeyPressHandler}
                value={newTask}
                className={ error ? 'error' : ''}/>
                <button onClick={addNewTaskHandler}>
                    +
                </button>
                {
                    error&&<div className='error-message'>Field is required</div>
                }
            </div>
            <ul>
                {
                    tasks.map((task)=>{

                        const removeTaskHandler = () => {
                            removeTask(task.id)
                        }
                        const changeCheckedHandler = () =>{
                            changeChecked(task.id)
                        }

                        return (
                            <li key={task.id}>
                                <input type='checkbox'
                                className='error'
                                checked={task.isDone}
                                onClick={changeCheckedHandler}
                                />
                                <span>{task.title}</span>
                                <button onClick={removeTaskHandler}>x</button>
                            </li>
                        )
                    })
                }
            </ul>
            <div>
                <button onClick={changeFilterAllHandler} 
                className={allBtn ? 'active-btn' : ''}>
                    All
                </button>
                <button onClick={changeFilterActiveHandler}
                className={activeBtn ? 'active-btn' : ''}>
                    Active
                </button>
                <button onClick={changeFilterCompletedHandler}
                className={completedBtn ? 'active-btn' : ''}>
                    Completed
                </button>
            </div>
        </div>
    )
}
