import { debug } from "console"
import React from "react"
export type TaskType = {
    id:number
    title:string
    isDone:boolean
}
type PropsType = {
    title:string
    tasks:Array<TaskType>
    removeTask: Function
    removeAllTask: Function
    removeCompletedTask:Function
    removeActiveTask:Function
}

export const Todolist = ({title,tasks,removeTask,removeAllTask,removeCompletedTask,removeActiveTask}:PropsType)=>{
    return (
        <div>
            <h3>
                {title}
            </h3>
            <div>
                <input/>
                <button>+</button>
            </div>
            <ul>
                {
                    tasks.map((task)=>{
                        return (
                            <li>
                                <input type='checkbox' checked={task.isDone}/>
                                <span>{task.title}</span>
                                <button onClick={()=>{removeTask(task.id)}}>x</button>
                            </li>
                        )
                    })
                }
            </ul>
            <div>
                <button onClick={()=>{removeAllTask}}>All</button>
                <button>Active</button>
                <button>Completed</button>
            </div>
        </div>
    )
}
