import React, { useState } from 'react';
import './App.css';
import { TaskType, Todolist } from './components/Todolist/Todolist/Todolist';
import { v1 } from 'uuid';

export type FilterValuesType ="all" | "completed" | "active"

function App() {
  let [tasks,setTasks]=useState< Array <TaskType> >([
    {id:v1(), title:'CSS', isDone:true},
    {id:v1(), title:'JS', isDone:true},
    {id:v1(), title:'React', isDone:false},
    {id:v1(), title:'Redux', isDone:false}
  ])
  let [filter,setFilter] = useState<FilterValuesType>("all")

  let [newTask,setNewTask] = useState('')
  const CreateNewTask = (value:string)=>{
    setNewTask(value)
  }


  let tasksForToDoList = tasks
  if(filter==="completed"){
    tasksForToDoList=tasks.filter((t:TaskType)=>{return t.isDone===true})
  }

  if(filter==="active"){
    tasksForToDoList=tasks.filter((t:TaskType)=>{return t.isDone===false})
  }

  let removeTask = (id:string)=>{
    setTasks(tasks=tasks.filter((t:TaskType) => {return (t.id!==id)}))
  }

  let changeFilter = (filter:FilterValuesType)=>{
    setFilter(filter)
  }
  return (
    <div className="App">
      <Todolist title={'What to learn'} 
        tasks={tasksForToDoList} 
        removeTask={removeTask}
        changeFilter={changeFilter}
        CreateNewTask={CreateNewTask}/>
    </div>
  );
}


export default App;
