import React from 'react';
import { FilterValuesType } from "../../../App"
import './Todolist.css'
import AddNewTask from '../../AddNewTask/AddNewTask';
import EditableSpan from '../../EditableSpan/EditableSpan';
import { Button } from '@mui/material';
export type TaskType = {
    id:string
    title:string
    isDone:boolean
}
type PropsType = {
    title:string
    tasks:Array<TaskType>
    removeTask: (id:string, todolistId:string)=> void
    changeFilter: (filter:FilterValuesType, todolistId:string)=> void
    addNewTask:(value:string,id:string)=>void
    changeChecked:(id:string,todolistId:string)=>void
    filter:FilterValuesType
    id:string
    deleteTask:(id:string)=>void
    changeTaskTitle:(taskId:string,todolistId:string, newValue:string)=>void
    changeTodolistTitle:(todolistId:string, newValue:string)=>void
}

export const Todolist = ({title,tasks,removeTask,changeFilter,addNewTask,changeChecked,filter, id,deleteTask,changeTaskTitle,changeTodolistTitle}:PropsType)=>{

    const changeFilterAllHandler = () => {
        changeFilter('all',id)
    }
    const changeFilterActiveHandler = () => {
        changeFilter('active',id)
    }

    const changeFilterCompletedHandler = () => {
        changeFilter('completed',id)
    }

    const deleteTaskHandler = () =>{
        deleteTask(id)
    }

    const addTask = (title:string)=>{
        addNewTask(title, id)
    }

    const onChangeTodolistTitelHandler = (newValue:string)=>{
        changeTodolistTitle(id,newValue)
    }

    return (
        <div>
            <div className='name-container'>
                <h3>
                    <EditableSpan 
                    title={title} 
                    isDone={false} 
                    onChange={onChangeTodolistTitelHandler}/>
                </h3>
                <Button
                    mt={3}
                    className='btn-delete-task'
                    onClick={deleteTaskHandler}>x</Button>
            </div>
            <AddNewTask addItem={addTask}/>
            <ul>
                {
                    tasks.map((task)=>{

                        const removeTaskHandler = () => {
                            removeTask(task.id,id)
                        }
                        const changeCheckedHandler = () =>{
                            changeChecked(task.id, id)
                        }
                        const onChangeTaskTitleHandler=(newValue:string)=>{
                            changeTaskTitle(task.id,id,newValue)
                        }

                        return (
                            <li key={task.id}>
                                <input type='checkbox'
                                className={task.isDone ? 'is-done' : ''}
                                checked={task.isDone}
                                onClick={changeCheckedHandler}
                                />
                                <EditableSpan title={task.title} 
                                isDone={task.isDone} 
                                onChange={onChangeTaskTitleHandler}/>
                                <button onClick={removeTaskHandler}>x</button>
                            </li>
                        )
                    })
                }
            </ul>
            <div>
                <button onClick={changeFilterAllHandler} 
                    className={filter==='all' ? 'active-btn' : ''}>
                    All
                </button>
                <button onClick={changeFilterActiveHandler}
                    className={filter==='active' ? 'active-btn' : ''}>
                    Active
                </button>
                <button onClick={changeFilterCompletedHandler}
                    className={filter==='completed' ? 'active-btn' : ''}>
                    Completed
                </button>
            </div>
        </div>
    )
}
